programa:

crear_Matriz:
li  $t1,15 # ancho de la matriz
li  $t2,10 # alto de la matriz
li  $t5, 4
mul $a0, $t1, $t2
mul $a0, $a0, $t5
li  $v0, 9
syscall
move $s0,$v0   # save array address in $s0

llenar_Matriz: 
add $t6,$s0,$zero
li $t3,0 # valor a llenar
li $t7,0 # llena desde 0 hasta 150 (t8)
mul $t8,$t1,$t2 # multiplica filas por columnas
j loop_llenado 

loop_llenado: # loop que llena la matriz con 0
sw $t3,($t6)
addi $t6,$t6,4
addi $t7,$t7,1
bne $t7,$t8,loop_llenado
j dibujar_Matriz

dibujar_Matriz:

#j pintar_Matriz

la $a0,cuadro_blanco # carga la direccion del cuadro_blanco
la $a1,display # carga la direccion del display

pintar_Matriz:
move $t0,$s0 # direccion del array
add $t6,$a0,$zero # direccion del cuadro blanco
add $t7,$a1,$zero # direccion del display
addi $t5, $zero, 0		# columna actual

li $t9,0
li $t2,150
while_d:
lw $t1,($t0) 			# cargamos lo que hay en la matriz
beq $t1,0,pintar_Cuadrado	# si hay un 0 en el cuadrado de la matriz, pintamos blanco
cuadro_ya_pintado:
addi $t6,$t6,4			# avanzamos a a[x][y+1]
add $t7,$t7,100			#
addi $t9,$t9,1
bne $t9,$t2,while_d
fin_while_d:
j fin_pintar_matriz

pintar_Cuadrado:

WHILE_COLUMNA:                     # mientras mostremos una columna
lw $t1, 0($t6)                     # cargamos la palabra de la direccion dem meoria del array
beq $t1, 16777216, SALTAR_FILA            # si nos encontramos con un cero hay que saltar a la siguiente fila
sw $t1, 0($t7)                     # si no nos saltamos la fila, entonces copiamos el valor al display
addi $t6, $t6, 4                   # nos movemos una palabra en el array
addi $t7, $t7, 4                   # nos movemos una palabra en el display
j WHILE_COLUMNA

SALTAR_FILA:                        # Nos vamos a saltar una fila, pero debemos revisar si no hemos terminado (dos ceros seguidos)
addi $t6, $t6, 4                    # Nos movemos al siguiente elemento del array
lw $t1, 0($t6)                      # cargamos el siguiente elemento del array
beq $t1, 0, FIN_MOSTRAR             # ¿Hay un cero?, entonces encontramos dos ceros seguidos y terminamos

addi $t5, $t5, 1                    # ¿no encontramos un cero?, entonces nos movemos a la siguiente columa

mul $t1, $t5, 2048                  # la direccion de la siguiente columna es: NUMERO_COLUMNA * 2048), 2048 ya que es un array de words: 4*512

add $t7, $a1, $t1                   # la nueva direccion del display es: NUMERO_COLUMNA * 2048 + Display => offset + display
j WHILE_COLUMNA                     # mostramos la siguiente columa
FIN_MOSTRAR:
j cuadro_ya_pintado

fin_pintar_matriz:
j fin_programa

li $t0,0
li $t1,1
li $t2,2
li $t3,3
li $t4,4

lw $t9,($s0)
loopasd:
beq $t9,$t0,pintar_blanco
beq $t9,$t1,pintar_verde
beq $t9,$t2,pintar_azul
beq $t9,$t3,pintar_rosado
beq $t9,$t4,pintar_hola

pintar_blanco:
pintar_verde:
pintar_azul:
pintar_rosado:
pintar_hola:

fin_programa:
